import React from 'react';
import './App.css';

function App() {
  state={
    userInput:[]
  }
  changHandler=(e)=>{
    this.setState({userInput:e.target.value});
  }
  return (
    <div className="App">
      <input 
      type='text' 
      onChange={this.changHandler()} 
      value={this.state.userInput} 
      />
      <p>{this.state.userInput}</p>
    </div>
  );
}

export default App;
