import React, {Component} from 'react';
import './App.css';

class App extends Component {
  state={
    userInput:''
  }
  changHandler=(e)=>{
    this.setState({userInput:e.target.value});
  }
  render(){
  return (
    <div className="App">
      <input 
      type='text' 
      onChange={this.changHandler} 
      value={this.state.userInput} 
      />
      <p>{this.state.userInput}</p>
    </div>
  );
}
}

export default App;
